window.addEventListener('load',function(){if('performance'in window){const perfData=performance.getEntriesByType('navigation')[0];if(perfData){const metrics={loadTime:Math.round(perfData.loadEventEnd-perfData.loadEventStart),domContentLoaded:Math.round(perfData.domContentLoadedEventEnd-perfData.domContentLoadedEventStart),firstPaint:0,firstContentfulPaint:0};const paintEntries=performance.getEntriesByType('paint');paintEntries.forEach(entry=>{if(entry.name==='first-paint'){metrics.firstPaint=Math.round(entry.startTime);}if(entry.name==='first-contentful-paint'){metrics.firstContentfulPaint=Math.round(entry.startTime);}});if(window.PerformanceObserver){const observer=new PerformanceObserver(list=>{list.getEntries().forEach(entry=>{if(entry.entryType==='largest-contentful-paint'){metrics.largestContentfulPaint=Math.round(entry.startTime);}});});try{observer.observe({entryTypes:['largest-contentful-paint']});}catch(e){}}console.log('Performance Metrics:',metrics);if(sessionStorage){sessionStorage.setItem('performanceMetrics',JSON.stringify(metrics));}}}});