document.addEventListener('DOMContentLoaded',function(){const lazyImages=document.querySelectorAll('img[data-src]');const lazyContent=document.querySelectorAll('.lazy-load');if('IntersectionObserver'in window){const imageObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){const img=entry.target;if(img.dataset.src){img.src=img.dataset.src;img.removeAttribute('data-src');img.classList.add('loaded');}imageObserver.unobserve(img);}});},{rootMargin:'50px 0px'});const contentObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('lazy-loaded');contentObserver.unobserve(entry.target);}});},{rootMargin:'100px 0px'});lazyImages.forEach(img=>{img.classList.add('lazy');imageObserver.observe(img);});lazyContent.forEach(el=>contentObserver.observe(el));}else{lazyImages.forEach(img=>{if(img.dataset.src){img.src=img.dataset.src;img.removeAttribute('data-src');}});lazyContent.forEach(el=>el.classList.add('lazy-loaded'));}});